    @GetMapping("/channels")
    public List<Channel> getChannels(){
        return channelService.getAllChannels();
    }
    @GetMapping("/translation-objects")
    public List<TranslationObject> getTranslationObjects(){
        return objectService.getAllObjects();
    }
    @GetMapping("/appeal-types")
    public List<AppealType> getAppealTypes(){
        return appealTypeService.getAllObjects();
    }
    @GetMapping("/appeal-statuses")
    public List<AppealStatus> getAppealStatuses(){
        return appealStatusService.getAllStatuses();
    }
    @GetMapping("/appeals")
    public List<Appeal> getAppeals(){
        return appealService.getAllAppeals();
    }
    @GetMapping("/appeals-by-mail")
    public List<Appeal> getAppealsByEmail(@RequestParam String email){
        return appealService.getAllAppealsByEmail(email);
    }
    @GetMapping("/questions")
    public List<Question> getQuestions(){
        return questionService.getAll();
    }
    @GetMapping("/tv-channels")
    public List<TVChannel> getTVChannels(){
        return tvChannelService.getAll();
    }
    @GetMapping("/tv-programs")
    public List<TVProgram> getTVPrograms(){
        return tvProgramService.getAll();
    }
    @GetMapping("/tv-programs-by-date")
    public List<TVProgram> getTVProgramsByDate(@RequestParam String date){
        Date parsedDate = null;
        try{
            parsedDate = formatter.parse(date);
        }catch (ParseException ex){
            System.out.println("!!!-=-=-=COULD NOT PARSE DATE-=-=-=-!!!");
            parsedDate = new Date();
        }
        return tvProgramService.getAllByDate(parsedDate);
    }

    @PostMapping("/save-appeal")
    public Appeal saveAppeal(@RequestBody Appeal appeal){
        return appealService.save(appeal);
    }


