    private final void tvProgramParse(TVChannel channel, String date ,String data){
        String[] ps = data.split("\n");
        for(String el:ps){
            String[] els = el.split("\\*");
            System.out.println(els[0]+" "+els[1]);
            tvProgramService.save(new TVProgram(els[1], date+", "+els[0], channel));
        }
    }
